mapper.select:
select
article_id, article_title, article_contextHtml, article_contextMd, cover_image, isComment, isOriginal, isTop, status, keywords
from timeblog.time_article


        and article_id = #{articleId}


        and article_title = #{articleTitle}


        and article_contextHtml = #{articleContextHtml}


        and article_contextMd = #{articleContextMd}


        and cover_image = #{coverImage}


        and isComment = #{isComment}


        and isOriginal = #{isOriginal}


        and isTop = #{isTop}


        and status = #{status}


        and keywords = #{keywords}
