mapper.select:
select
  blog_title, blog_name, blog_style, blog_create_time, blog_author_mail, blog_author_qq, blog_author_wx, index_top_article_type, index_background
from timeblog.time_web_config


        and blog_title = #{blogTitle}


        and blog_name = #{blogName}


        and blog_style = #{blogStyle}


        and blog_create_time = #{blogCreateTime}


        and blog_author_mail = #{blogAuthorMail}


        and blog_author_qq = #{blogAuthorQq}


        and blog_author_wx = #{blogAuthorWx}


        and index_top_article_type = #{indexTopArticleType}


        and index_background = #{indexBackground}
