mapper.select:
select
  comment_id, comment_picture, comment_content, comment_nickname, comment_ip, comment_address, create_time,comment_status
from timeblog.time_comment


        and comment_id = #{commentId}


        and comment_picture = #{commentPicture}


        and comment_content = #{commentContent}


        and comment_nickname = #{commentNickname}


        and comment_ip = #{commentIp}


        and comment_address = #{commentAddress}


        and comment_article_id = #{commentArticleId}


        and comment_status = #{commentStatus}


        and create_time = #{createTime}


order by create_time desc
